validate.custom:
function isValid(value){

  let valid = true;
  // Get total land values
  const totalLandValue = data.land_size || 0;
  const totalLandUnit = data.land_size_unit || 'ha';

  // Get agricultural land values
  const agriLandValue = value || 0;
  const agriLandUnit = data.agricultural_land_size_unit || 'ha';

  // Convert to same unit (ha)
  let totalInHa = totalLandValue;
  if (totalLandUnit === 'km2') {
    totalInHa = totalLandValue * 100;
  }

  let agriInHa = agriLandValue;
  if (agriLandUnit === 'km2') {
    agriInHa = agriLandValue * 100;
  }

  // Compare
  if (agriInHa > totalInHa) {
    valid = 'Agricultural land cannot exceed total land size';
  }

  return valid;
}
valid = isValid(input)

